import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(
        'http://localhost:5000/auth/register',
        { email, password },
        { withCredentials: true }
      );

      if (res.data.message === 'User registered successfully') {
        setMessage('Registration successful! Please log in.');
        setTimeout(() => navigate('/'), 2000);
      } else {
        setMessage(res.data.message);
      }
    } catch (err) {
      setMessage(err.response?.data?.message || 'An error occurred. Please try again.');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center p-8 bg-gray-100 min-h-screen">
      <h2 className="text-2xl font-semibold mb-4">Register</h2>
      <form
        onSubmit={handleRegister}
        className="flex flex-col gap-4 p-6 bg-white rounded-lg shadow-md w-full max-w-sm"
      >
        <label className="flex flex-col">
          Email
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="p-2 border rounded-md"
          />
        </label>
        <label className="flex flex-col">
          Password
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className="p-2 border rounded-md"
          />
        </label>
        <button
          type="submit"
          className="bg-blue-500 text-white p-2 rounded-md hover:bg-blue-600 transition-colors"
        >
          Register
        </button>
      </form>
      {message && (
        <p
          className={`mt-4 ${message.includes('successful') ? 'text-green-500' : 'text-red-500'}`}
        >
          {message}
        </p>
      )}
      <p className="mt-4">
        Already have an account?{' '}
        <Link to="/" className="text-blue-500 hover:underline">
          Login here
        </Link>
      </p>
    </div>
  );
};

export default Register;
